import {Container, createTheme, LinearProgress, Table, TableContainer, TableHead, TableRow, TextField, Typography, TableCell, TableBody, makeStyles, ThemeProvider } from "@material-ui/core";
import axios from "axios";
import { useEffect, useState } from "react";
import { CoinList } from "../api";
import { CryptoState } from "../context";
import { useNavigate } from "react-router-dom";
import { Pagination } from "@material-ui/lab";

function numberWithCommas(x) {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}
function CTable() {
    const [coins, setCoins] = useState([])
    const [loading, setLoading] = useState(false)
    const [search, setSearch] = useState("");
    const[page, setPage] = useState(1);
    const {currency, symbol} = CryptoState();
    const navigate = useNavigate();
    const getcoins = async () => {
        setLoading(true);
        const {data} = await axios.get(CoinList(currency))
        setCoins(data);
        setLoading(false);
    }
    useEffect(()=> {
        getcoins();

    }, [currency])

    const darkTheme = createTheme({
        palette: {
          primary: {
            main: "#fff",
          },
          type: "dark",
        },
      });

      const handleSearch = () => {
        return coins.filter(
          (coin) =>
            coin.name.toLowerCase().includes(search.toLowerCase().trim()) || coin.symbol.toLowerCase().includes(search.toLowerCase().trim())
        );
      };
    const useStyles = makeStyles({
        row: {
          backgroundColor: "#16171a",
          cursor: "pointer",
          "&:hover": {
            backgroundColor: "#131111",
          },
          fontFamily: "Montserrat",
        },
        pagination: {
          "& .MuiPaginationItem-root": {
            color: "gold",
          },
        },
      });

    const classes = useStyles();
    return (
        <ThemeProvider theme = {darkTheme}>
        <Container style = {{textAlign: "center"}}>
        <TextField 
            label = "Search for a Coin" variant="outlined"
            style={{marginBottom: 40, width: "100%", marginTop: 40}}
            color="red"
            onChange={(e)=>{setSearch(e.target.value)}}
        />
        <TableContainer>
            {
                loading ? (
                    <LinearProgress style = {{backgroundColor:"gold"}}/>
                ) : (
                    <Table aria-label="simple table">
                        <TableHead style = {{backgroundColor:"#EEBC1D"}}>
                        <TableRow>
                        {["Coin", "Price", "24h Change", "Market Cap"].map((head) => (
                            <TableCell
                                style={{
                                color: "black",
                                fontWeight: "700",
                                fontFamily: "Montserrat",
                                }}
                            key={head}
                            align={head === "Coin" ? "" : "right"}
                            >
                      {head}
                    </TableCell>
                  ))}
                        </TableRow>
                        </TableHead>
                        <TableBody>
                        {handleSearch().slice((page - 1) * 10, (page - 1) * 10 + 10).map((row) => {
                            const profit = row.price_change_percentage_24h > 0;
                            return (
                                <TableRow 
                                onClick = {() => {
                                    navigate(`/coins/${row.id}`)
                                }}
                                className={classes.row}
                                key = {row.name}
                                >
                                <TableCell component = 'th' scope = 'row' style = {{display: "flex", gap: 15}}>
                                <img
                            src={row?.image}
                            alt={row.name}
                            height="50"
                            style={{ marginBottom: 10 }}
                          />
                           <div
                            style={{ display: "flex", flexDirection: "column" }}
                          >
                            <span
                              style={{
                                textTransform: "uppercase",
                                fontSize: 22,
                              }}
                            >
                              {row.symbol}
                            </span>
                            <span style={{ color: "darkgrey" }}>
                              {row.name}
                            </span>
                          </div>
                                </TableCell>
                        <TableCell align="right">
                          {symbol}{" "}
                          {numberWithCommas(row.current_price.toFixed(2))}
                        </TableCell>
                                <TableCell
                          align="right"
                          style={{
                            color: profit > 0 ? "green" : "red",
                            fontWeight: 500,
                          }}
                        >
                          {profit && "+"}
                          {row.price_change_percentage_24h.toFixed(2)}%
                        </TableCell>
                        <TableCell align="right">
                          {symbol}{" "}
                          {numberWithCommas(
                            row.market_cap.toString().slice(0, -6)
                          )}
                          {" "}M
                        </TableCell>
                                </TableRow>
                            )
                        })}
                        </TableBody>
                    </Table>
                )
            }
        </TableContainer>
        <Pagination 
            style={{
            padding: 20,
            width: "100%",
            display: "flex",
            justifyContent: "center",
          }}
          classes={{ ul: classes.pagination }}
            count={(handleSearch()?.length/10).toFixed(0)}
            onChange={(_, value) => {
            setPage(value);
            window.scroll(0, 450);
          }}
        />
        </Container>
        </ThemeProvider>
    )
}

export default CTable;
