import axios from "axios";
import { useEffect, useState } from "react";
import { HistoricalChart } from "../api";
import { CryptoState } from "../context";
import {CircularProgress, createTheme, ThemeProvider } from "@material-ui/core";
import {Line} from 'react-chartjs-2';
import {makeStyles} from '@material-ui/core'
import Button from './Button'
const Coininfo= ({coin}) => {
    const chartdays = [
        {
          label: "24 Hours",
          value: 1,
        },
        {
          label: "30 Days",
          value: 30,
        },
        {
          label: "3 Months",
          value: 90,
        },
        {
          label: "1 Year",
          value: 365,
        },
      ];
    const darkTheme = createTheme({
        palette: {
          primary: {
            main: "#fff",
          },
          type: "dark",
        },
      });
      const useStyles = makeStyles((theme) => ({
        container: {
          width: "75%",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          marginTop: 25,
          padding: 40,
          [theme.breakpoints.down("md")]: {
            width: "100%",
            marginTop: 0,
            padding: 20,
            paddingTop: 0,
          },
        },
      }));
      const classes = useStyles();
    const[hist, setHist] = useState();
    const [interval, setInterval] = useState(1);
    const {currency, symbol} = CryptoState();
    const getHistData = async () => {
        const {data} = await axios.get(HistoricalChart(coin.id, interval, currency))
        setHist(data.prices)
    }
    useEffect(() => {
        getHistData()
    }, [currency, interval])

return (
    <ThemeProvider theme={darkTheme}>
        <div className={classes.container}>
            {
                !hist ? (
                    <CircularProgress style = {{color: "gold"}} size = {250} thickness={1}/>
                ) : (
                    <>
                    {/* <Line
                    data = {{
                        labels: hist.map((coin) => {
                            var date = new Date(coin[0]);
